function extractValue(current){
  const sr = new SimpleRecord("itam_m2m_task_asset");
  sr.addQuery("task_id", current.sys_id);
  sr.selectAttributes("asset_id");
  sr.query();
  const arr = [];
  while(sr.next()) {
    arr.push(String(sr.asset_id.state));  
  }
  return arr;
}

